mapper.select:
SELECT COUNT(*) FROM TBL_BOARD TB
AND TB.usr_no=#{usr_no}
AND TB.usr_no is null

mapper.update:
UPDATE TBL_BOARD
   SET brdtitle=#{brdtitle}
     , brdwriter=#{brdwriter}
     , brdmemo=#{brdmemo}
WHERE BRDDELETEFLAG='N'
  AND BRDNO=#{brdno}

mapper.delete:
	UPDATE TBL_BOARD
        SET BRDDELETEFLAG='Y'
    WHERE BRDNO=#{brdno}

DELETE
    FROM TBL_BOARDFILE
    WHERE fileno IN (

            ${item}

)

mapper.update:
UPDATE TBL_BOARD
   SET BRDHIT = BRDHIT + 1
 WHERE BRDNO=#{brdno}
