mapper.insert:
insert into user_login_history(
user_id,
log_type
) values (
#{user_id},
#{log_type}
)

mapper.update:
update user set
    email = #{email},
	phone = #{phone},
	password = #{password}
where user_id = #{user_id}

mapper.select:
SELECT COUNT(*)
    FROM user A where 1=1


        A.${item} LIKE CONCAT('%', #{searchKeyword},'%' )


 AND A.user_name= #{user_name} 
 AND A.phone= #{user_id}

